mapper.select:
SELECT DEPARTMENT_TITLE
FROM DEPARTMENT_MANAGEMENT
WHERE DEPARTMENT_NO = #{deptNo}

SELECT *
FROM DEPARTMENT_MANAGEMENT
WHERE DEPARTMENT_NO != #{deptNo}

SELECT DOC_TYPE
FROM APRV_DOCUMENT
WHERE DOC_NO = #{docNo}

mapper.update:
UPDATE APRV_DOCUMENT
SET APRV_STATUS = #{aprvStatus}
WHERE DOC_NO = #{docNo}

mapper.select:
SELECT DRAFTER
FROM APRV_DOCUMENT
WHERE DOC_NO = #{docNo}

mapper.update:
UPDATE APRV_DOCUMENT
SET STATUS = 'N'
WHERE DOC_NO = #{docNo}

mapper.select:
SELECT COUNT(*)
FROM V_APRV_HISTORY
WHERE APPROVER = #{drafter}

	AND APRV_STATUS = #{aprvStatus}

SELECT A.DOC_NO, A.DOC_FORM, A.DOC_TITLE, A.DRAFTER_NAME, A.DFT_DATE, A.APRV_STATUS, B.APRV_STATUS AS APRV_STATUS_NAME FROM V_APRV_HISTORY A JOIN APRV_STATUS B ON A.APRV_STATUS = B.APRV_STATUS_NO WHERE A.APPROVER = #{drafter}
AND A.APRV_STATUS = #{aprvStatus}
ORDER BY A.PRO_DATE DESC

SELECT APRV_STATUS
FROM APRV_DOCUMENT
WHERE DOC_NO = #{docNo}

SELECT DOC_TYPE
FROM DOC_OUTBOX
WHERE DOC_OUTBOX_NO = #{outboxNo}

mapper.update:
UPDATE DOC_OUTBOX SET LAST_UPDATE_DATE = SYSDATE WHERE 1=1
AND DRAFTER = #{drafter} AND DOC_OUTBOX_NO = (SELECT MAX(DOC_OUTBOX_NO) FROM DOC_OUTBOX WHERE DRAFTER = #{drafter})
DOC_OUTBOX_NO = #{docOutboxNo}

		UPDATE LEAVE_APP
