mapper.select:
SELECT
    A.START_DATE,
	A.END_DATE,
	A.WRITER_NO,
	A.SECTION_NUM,
	B.COLOR_CODE,
	A.DATE_NAME,
	A.PUBLIC_RANGE,
	A.NOTICE_TIME,
	A.PLACE,
	A.MEMO,
	A.REALWRITER_NO
FROM CALENDER A
JOIN CAL_COLOR B ON (A.COLOR_NUM = B.COLOR_NUM)
 WHERE WRITER_NO = 10000
	AND (EXTRACT(YEAR FROM START_DATE) <= #{year}
	AND EXTRACT(MONTH FROM START_DATE) <= #{month}
	AND EXTRACT(YEAR FROM END_DATE) >= #{year}
	AND EXTRACT(MONTH FROM END_DATE) >= #{month}
	AND A.STATUS = 'Y'
	)

	OR WRITER_NO = #{empNo}
	AND (EXTRACT(YEAR FROM START_DATE) <= #{year}
	AND EXTRACT(MONTH FROM START_DATE) <= #{month}
	AND EXTRACT(YEAR FROM END_DATE) >= #{year}
	AND EXTRACT(MONTH FROM END_DATE) >= #{month}
	AND A.STATUS = 'Y'
	)


	OR WRITER_NO = #{departmentNo}
	AND (EXTRACT(YEAR FROM START_DATE) <= #{year}
	AND EXTRACT(MONTH FROM START_DATE) <= #{month}
	AND EXTRACT(YEAR FROM END_DATE) >= #{year}
	AND EXTRACT(MONTH FROM END_DATE) >= #{month}
	AND A.STATUS = 'Y'
	)

mapper.update:
UPDATE CALENDER SET
STATUS = 'N'
WHERE 1=1
AND TO_CHAR(START_DATE,'YYYY/MM/DD HH24:MI') = #{startDate}
AND TO_CHAR(END_DATE,'YYYY/MM/DD HH24:MI') = #{endDate}
AND REALWRITER_NO = #{realWriter}

UPDATE CALENDER SET START_DATE = TO_DATE(#{startDate},'YYYY/MM/DD HH24:MI'), END_DATE = TO_DATE(#{endDate},'YYYY/MM/DD HH24:MI'), WRITER_NO = ${writerNo}, SECTION_NUM = ${sectionOption}, COLOR_NUM = ${selectColor}, DATE_NAME = #{title}, PUBLIC_RANGE = #{openOption}, NOTICE_TIME = #{alarm}
, PLACE = #{place}
, PLACE = NULL
, MEMO = #{memo}
, MEMO = NULL
WHERE START_DATE = TO_DATE(#{editStartDate},'YYYY/MM/DD HH24:MI') AND END_DATE = TO_DATE(#{editEndDate},'YYYY/MM/DD HH24:MI') AND WRITER_NO = ${editWriterNo}
