mapper.update:
UPDATE ANNOUNCE_DEPART
SET ANNO_TITLE =#{annoTitle}, ANNO_CONTENT=#{annoContent}, ACCESS_REPLY=#{accessReply}, ATTACH_STATUS=#{attachStatus}
WHERE ANNO_NO = #{annoNo}
AND STATUS='Y'

UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName}, CHANGE_NAME = #{changeName}
WHERE FILE_NO=#{fileNo}
AND STATUS ='Y'

mapper.select:
SELECT FAV_PRO AS PRO_NO, PF_EMP AS PRO_WRITER
FROM PROJECT_FAV
WHERE FAV_PRO =#{proNo}
AND PF_EMP = #{proWriter}

mapper.delete:
DELETE PROJECT_FAV 
WHERE FAV_PRO =#{proNo}
AND PF_EMP = #{proWriter}

mapper.update:
UPDATE PRO_CLASS

	SET PC_ONE = #{changepc}


	SET PC_TWO = #{changepc}


	SET PC_THREE = #{changepc}


	SET PC_FOUR = #{changepc}

WHERE PC_NO =#{pcNo}
AND PC_STATUS = 'Y'

UPDATE SEMI_PRO
SET REF_PC=#{changepc}
WHERE REF_PC=#{originpc}
AND SEMI_STATUS = 'Y'

UPDATE SEMI_PRO

SET SEMI_TITLE =#{semiTitle}


SET SEMI_DUE = #{semiDue}

WHERE SEMI_NO = #{semiNo}
AND SEMI_STATUS = 'Y'

UPDATE PRO_WATCHER
SET PW_STATUS = 'N'
WHERE REF_PRO = #{proNo}
AND PW_ATTEN = #{proWriter}
AND  PW_STATUS = 'Y'

UPDATE SEMI_PRO

	SET SEMI_CONTENT = #{semiContent}


	SET SEMI_CONTENT = NULL

WHERE SEMI_NO = #{semiNo}
AND SEMI_STATUS = 'Y'

UPDATE SEMI_REPLY
SET SR_STATUS = 'N'
WHERE SR_NO = #{replyNo}
AND REF_SEMIPRO = #{refDepartNo}
AND  SR_STATUS = 'Y'

UPDATE SEMI_PRO
SET SEMI_STATUS = 'N'
WHERE SEMI_NO = #{semiNo}
AND REF_PRO = #{refPro}
AND  SEMI_STATUS = 'Y'

UPDATE ATTACHMENT
SET STATUS = 'N'
WHERE REF_LIST_CAT_NO = 61
AND FILE_NO = #{fileNo}
AND CHANGE_NAME = #{changeName}
AND  STATUS = 'Y'

UPDATE SEMI_PRO
SET REF_PC = #{refPc}
WHERE SEMI_NO = #{semiNo}
AND REF_PRO = #{refPro}
AND  SEMI_STATUS = 'Y'
