mapper.select:
	SELECT 
		A.RESERVE_NUM,
 		A.EMP_NO,
 		A.ROOM_SMALL_NO,
 		C.LROOM_NAME,
 		B.SROOM_NAME,
 		A.START_DATE,
 		A.END_DATE,
 		A.MEETING_NAME,
 		A.STATUS
	FROM RESERVE_LIST A
	JOIN MEETINGROOM_MANAGEMENT B ON (A.ROOM_SMALL_NO = B.ROOM_SMALL_NO)
	JOIN MEETINGROOM_CATEGORY_LARGE C ON (B.ROOM_LARGE_NO = C.ROOM_LARGE_NO)
	
		A.STATUS = 'Y'
		
			AND A.EMP_NO = #{empNo}
		
		
			AND (
				EXTRACT(YEAR FROM A.START_DATE) = #{searchYear}
				AND EXTRACT(MONTH FROM A.START_DATE) = #{searchMonth}
				AND EXTRACT(DAY FROM A.START_DATE) = #{searchDate}
				OR EXTRACT(YEAR FROM A.END_DATE) = #{searchYear}
				AND EXTRACT(MONTH FROM A.END_DATE) = #{searchMonth}
				AND EXTRACT(DAY FROM A.END_DATE) = #{searchDate}
				)
		
		
			AND A.ROOM_SMALL_NO = #{roomSmallNo}
		
	
	ORDER BY A.START_DATE

SELECT A.RESERVE_NUM, A.EMP_NO, A.ROOM_SMALL_NO, C.LROOM_NAME, B.SROOM_NAME, A.START_DATE, A.END_DATE, A.MEETING_NAME FROM RESERVE_LIST A JOIN MEETINGROOM_MANAGEMENT B ON (A.ROOM_SMALL_NO = B.ROOM_SMALL_NO) JOIN MEETINGROOM_CATEGORY_LARGE C ON (B.ROOM_LARGE_NO = C.ROOM_LARGE_NO)
A.STATUS = 'Y' AND EXTRACT(YEAR FROM A.START_DATE) = #{searchYear} AND EXTRACT(MONTH FROM A.START_DATE) = #{searchMonth} AND EXTRACT(DAY FROM A.START_DATE) = #{searchDate} AND A.ROOM_SMALL_NO = #{sRoom}
AND A.RESERVE_NUM != #{reserveNo}

mapper.update:
UPDATE RESERVE_LIST
SET STATUS = 'N'
WHERE RESERVE_NUM = #{reserveNo}

UPDATE RESERVE_LIST
SET ROOM_SMALL_NO = #{roomSmallNo},
	START_DATE = TO_DATE(#{startDate},'YYYY-MM-DD HH24:MI'), 
	END_DATE = TO_DATE(#{endDate},'YYYY-MM-DD HH24:MI'),
	MEETING_NAME = #{meetingName}
WHERE RESERVE_NUM = #{reserveNo}
